css_editor-general:
.vc_separator{
margin-bottom:20px !important;
text-align:left !important;
}
#cambiador span{
line-height:18px !important;
margin-top:40px !important;
}



/*FORM*/
#jugadores span{
color:#55BADB;
font-size:20px !important;
width:30px !important;
display:inline-block !important;
}
#jugadores input{
margin-left:20px;
width:25% !important;;
}

#contacto input{
width:80%;
background:#E8E8E8;
border:0px !important;
padding:5px;
margin-bottom:20px;
}
#jugadores{
height:400px !important;
overflow: scroll;
}
.jugadores textarea{
width:80% !important;
background:#E8E8E8;
border:0px;
padding:10px;
color:grey;

}

/*short*/
.short{display: none;}
.cuadraditos{
text-align:center ;
}


/*MODELOS*/
#cambiador #modelos{
  margin-bottom: 50px;
  text-align: center;
}

#cambiador #modelos li img{
width:12% !important;
}
#cambiador #modelos li{
display:inline;
}


#cambiador span{
font-size:14px !important;
}
#cambiador #tipos{
padding:0px;
}
#cambiador #tipos li{
display:inline-block;
width:49% !important;
}

#cambiador #cuellos .borde{
border:2px solid #55BADB;
border-radius:40px;
}

.borde{
border:1px solid #55BADB;
}
#modelos li.borde{
border:0px solid #55BADB !important;
}
#modelos li.borde img{
border:1px solid #55BADB !important;
}

/*CLAVES*/
.img_frentes{
width:400px !important;
height:400px !important;
position: absolute;
top: 0px;
left: 0px;
}

.baseimg{
width:400px !important;
height:400px !important;

}


@media (max-width: 799px) {
  .mipanel{
  margin-bottom:50px !important;
  }
.vc_column-inner,.baseimg{
  padding:0px !important;
  }

}

#msg{
margin-top:30px;
}

#msg span{
display:block;
text-align:left;
font-size:18px !important;
line-height: 28px !important;
margin: 0 !important;
font-weight:bold !important;
}




/*Numeros arranca mal*/
#img_numeros{
  display: none;
}

#cambiador .cuellos li{
display:inline-block !important;
}


#cambiador li{
list-style-type: none;
}
#cambiador .row {
  display:flex;
  align-items:top;
}

#cambiador  .colores{
background: #E8E8E8;
width:80%;
margin:0 auto !important;
margin-top:40px !important;
padding-top:20px !important;
padding-bottom:24px !important;
}
#cambiador .mipanel{
text-align:center;
}

#cambiador .cuadradito{
background:#E8E8E8;
width:35%;
display:inline-block;
padding:10px;
text-align:center;
color:grey;
}


div,a,input,button{
  cursor:pointer !important;
}
/**/
#cambiador .boton{
text-align:center !important;
background:#56BADA;
color: white !important;
padding:10px;
display:inline-block ;
width:100px;
margin-top:30px;
}




.colores_list {
  margin-bottom: 11px;
  display: inline-block; 
  padding:10px !important; 

}

.colores_list li {
  float: left;
  margin: 0 5px 5px 0;
  width: 22px;
  height: 22px;
  border-radius: 2px;
  -moz-border-radius: 2px;
  -webkit-border-radius: 2px;
  cursor: pointer; }

.colores_list li.border_si {
  border: 1px solid #c2c2c2;
  width: 22px;
  height: 22px; }


.c2color{display:none;}
.c3color{display:none;}

.options span {
  display: block;
  margin: 18px 0 13px 0;
  text-transform: uppercase;
  font-weight: bold; }

.sin_nombre input, .sin_nombre label {
  float: left; }

.sin_nombre label {
  margin: 1px 0 0 5px !important;
  text-transform: uppercase;
  font-weight: 700;
  cursor: pointer; }

